import React from 'react';
import { Image, StyleSheet, View } from 'react-native';

interface BrisaLogoProps {
  width?: number;
  height?: number;
  style?: any;
}

// Base64 encoded SVG of Brisa Logo
const BRISA_LOGO_BASE64 = 'data:image/svg+xml;base64,';

export const BrisaLogo: React.FC<BrisaLogoProps> = ({ 
  width = 120, 
  height = 32, 
  style 
}) => {
  const aspectRatio = 97 / 26; // Original SVG aspect ratio
  const finalHeight = height;
  const finalWidth = width || finalHeight * aspectRatio;

  return (
    <View style={[styles.container, style]}>
      <Image
        source={{ uri: BRISA_LOGO_BASE64 }}
        style={[
          styles.logo,
          {
            width: finalWidth,
            height: finalHeight,
          }
        ]}
        resizeMode="contain"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    justifyContent: 'center',
    alignItems: 'flex-start',
  },
  logo: {
    // Logo will be sized by props
  },
});

export default BrisaLogo; 